import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../css/SingleImdeta.css";
import { useNavigate } from "react-router-dom";
const SingleImdeta = () => {
    let navigate = useNavigate()
  const { id } = useParams();
  const [singpagedeta, setSingpagedeta] = useState([""]);
  const [sameim, setSameim] = useState([""]);
  useEffect(() => {
    fetch(
      `https://pixabay.com/api/?key=38320105-b762e964374d782b30b2f8654&id=${id}`
    )
      .then((res) => res.json())
      .then((singpage) => {
        setSingpagedeta(singpage.hits);
        console.log(singpage.hits);
      });
  });

  useEffect(() => {
    fetch(
      `https://pixabay.com/api/?key=38320105-b762e964374d782b30b2f8654&q=${singpagedeta[0].tags}`
    )
      .then((res) => res.json())
      .then((sameitm) => {
        console.log(sameitm);
        setSameim(sameitm.hits);
      });
  });

  return (
    <>
      <div className="singlepage">
        {/*  */}
        <div className="Si-navbaar"></div>
        {/*  */}
        <div className="downloade-button">
          <button className="d-button">downloade</button>
        </div>
        {/*  */}
        <div className="show-img">
          <div className="center-img">
            <div className="details-img">
              <div className="view">
                <i className="fa-solid fa-eye"></i>
                <p className="view-number">{singpagedeta[0].views}</p>
              </div>

              <div className="like">
                <i className="fa-solid fa-heart"></i>
                <p className="like-number">{singpagedeta[0].likes}</p>
              </div>

              <div className="downloade">
                <i className="fa-solid fa-download"></i>
                <p className="downloade-number">{singpagedeta[0].downloads}</p>
              </div>
            </div>

            <img
              className="images-show"
              src={singpagedeta[0].webformatURL}
              alt="images.jpg"
            />
          </div>
        </div>

        {/* ============ same images ================ */}
        <div className="same-img">
          <div className="img-deta-same">
            {sameim.map((res) => {
              return (
                <>
