import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import "../css/SingleImdeta.css";
const SingleImdeta = () => {
  const { id } = useParams();

  useEffect(() => {
    fetch(
      `https://pixabay.com/api/?key=38320105-b762e964374d782b30b2f8654&id=${id}`
    )
      .then((res) => res.json())
      .then((singpage) => {
        console.log(singpage.hits);
      });
  });

  return (
    <>
      <h1>SingleImdeta id is : {id}</h1>
      <div className="singlepage">
        {/*  */}
        <div className="Si-navbaar"></div>
        {/*  */}
        <div className="downloade-button">
          <button className="d-button">downloade</button>
        </div>
        {/*  */}
        <div className="show-img">
          <div className="">
            <div className="details-img">
              <div className="view">
                <i class="fa-solid fa-eye"></i>
                <p className="view-number">21541</p>
              </div>

              <div className="like">
                <i class="fa-solid fa-heart"></i>
                <p className="like-number">21541</p>
              </div>

              <div className="downloade">
                <i class="fa-solid fa-download"></i>
                <p className="downloade-number">21541</p>
              </div>

              <div className="sve">
                <i class="fa-solid fa-bookmark"></i>
                <p className="save-number">21541</p>
              </div>
            </div>

            <img
              className="images-show"
              src="https://pixabay.com/get/g15d09a65dd3ed43de0ba88a3eae28ddf41e2753e790216aa0a13785538e811b9abe53b663340caa562570bc1b2da310ea361a28f3ab258fd7ca8918e6205bb9a_640.jpg"
              alt="images.jpg"
            />
          </div>
        </div>
      </div>
    </>
  );
};

export default SingleImdeta;
