import React, { useEffect, useState } from "react";
import Catogreis from "./Catogreis";
const ImagesresH = () => {
  const [data, setData] = useState([""]);
  const [page, setPage] = useState(1);
  const [url, setUrl] = useState(
    `https://pixabay.com/api/?key=38320105-b762e964374d782b30b2f8654&image_type=All&pretty=true&page=${page}`
  );

  useEffect(() => {
    fetch(url)
      .then((res) => res.json())
      .then((imgdata) => {
        setData((prev) => [...prev, ...imgdata.hits]);
      });
  }, [url, page]);

  const UpdateUrl = (deta) => {
    setUrl(
      `https://pixabay.com/api/?key=38320105-b762e964374d782b30b2f8654&image_type=${deta}&pretty=true`
    );
  };

  const handaleInfinitScroll = () => {
    console.log("Scrollheight" + document.documentElement.scrollHeight);
    console.log("ScrollTop" + document.documentElement.scrollTop);
    console.log("innerHeight" + window.innerHeight);
    if (
      window.innerHeight + document.documentElement.scrollTop + 1 >=
      document.documentElement.scrollHeight
    ) {
      setPage((prev) => prev + 1);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handaleInfinitScroll);
  }, []);

  return (
    <>
      <Catogreis updateurldeta={(deta) => UpdateUrl(deta)} />
      <div className="ImagesresH-box">
        <div className="imagesss">
          {data.map((res) => {
            // console.log(res.webformatURL);
            return (
              <>
                <div className="img-res" onClick={() => alert(res.id)}>
                  <img
                    className="on-img"
                    src={res.webformatURL}
                    alt="img.jpg"
                  />
                </div>
              </>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default ImagesresH;
